import logo from './logo.svg';
import './App.css';
import "bootstrap/dist/css/bootstrap.min.css";
import { Button } from 'reactstrap';
import { Component } from 'react';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      botoncitos: Array(5).fill('secondary'),
      cuantos : 0
    };
  }

  setSeleccionado(num){
    let aux = this.state.botoncitos;
    //aux[num] ='danger';
    if (aux[num].color !== "danger"){
      aux[num].color = "danger" ;
    }
    aux[num].cont = aux[num].cont + 1;
    this.setState({botones:aux});
    this.setState({botoncitos:aux});
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <h1>{this.state.cuantos}</h1>

          <div>
            <Botoncillo color={this.state.botoncitos[0]} />
            <Botoncillo color={this.state.botoncitos[1]} />
            <Botoncillo color={this.state.botoncitos[2]} />
            <Botoncillo color={this.state.botoncitos[3]} />
            <Botoncillo color={this.state.botoncitos[4]} />
          </div>
        </header>
      </div>
    );
  }
}
function Botoncillo(props) {
  return (
    // MUESTRA AQUÍ EL BOTÓN CON EL COLOR CORRESPONDIENTE
    <>
    <Button color={props.color}></Button>
    </>
  );
}
export default App;
