import logo from './logo.svg';
import './App.css';
import "bootstrap/dist/css/bootstrap.min.css";
import { Button } from 'reactstrap';
import { Component } from 'react';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      bontoncillos: Array(5).fill({count:0,color:'secondary'})
    };
  }

  alpulsar(boton){
    let aux = JSON.parse(JSON.stringify(this.state.botoncillos))
    if(aux[boton].color!=='danger'){
      aux[boton].color ='danger';
    }
    aux[boton].count =aux[boton].count+1;
    this.state({bontoncillos:aux});
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <div>
            <Botoncillo numBoton={0} cliquear={(b)=>this.alpulsar}></Botoncillo>
          </div>
        </header>
      </div>
    );
  }
}
function Botoncillo(props) {
  return (
    // MUESTRA AQUÍ EL BOTÓN CON EL COLOR CORRESPONDIENTE
    <Button color={props.color} onClick={(props.count)}/>
  );
}
export default App;
