import logo from './logo.svg';
import './App.css';
import "bootstrap/dist/css/bootstrap.min.css";
import { Button } from 'reactstrap';
import { Component } from 'react';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      botoncillos: Array(5).fill('secondary'),
      cont
    };
  }

  alpulsar(boton) {
    let aux = JSON.parse(JSON.stringify(this.state.botoncillos))
    if (aux[boton].color !== 'danger') {
      aux[boton].color = 'danger';
    }
    aux[boton].count = aux[boton].count + 1;
    this.state({ botoncillos: aux });
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <div>
            <Botoncillo numBoton={0} cliquear={(b) => this.alpulsar(b)} color={this.state.botoncillos[0].color} contador={this.state.botoncillos[0].cont} />

            <Botoncillo numBoton={1} cliquear={(b) => this.alpulsar(b)} color={this.state.botoncillos[1].color} contador={this.state.botoncillos[1].cont} />

            <Botoncillo numBoton={2} cliquear={(b) => this.alpulsar(b)} color={this.state.botoncillos[2].color} contador={this.state.botoncillos[2].cont} />

            <Botoncillo numBoton={3} cliquear={(b) => this.alpulsar(b)} color={this.state.botoncillos[3].color} contador={this.state.botoncillos[3].cont} />

            <Botoncillo numBoton={4} cliquear={(b) => this.alpulsar(b)} color={this.state.botoncillos[4].color} contador={this.state.botoncillos[4].cont} />

          </div>
        </header>
      </div>
    );
  }
}
function Botoncillo(props) {
  return (
    // MUESTRA AQUÍ EL BOTÓN CON EL COLOR CORRESPONDIENTE
    <Button color={props.color} onClick={() => props.cliquear(props.numBoton)}>{props.contador}</Button>
  );
}
export default App;
