import './App.css';
import 'bootstrap/dist/css/bootstrap.css';
import { Component } from 'react';
import Flashcard from './componentes/FlashcardComponent';
import { GODOS } from './shared/datos';
import { Button } from 'reactstrap';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      godos:GODOS,
    }
  }
  render() {
    return (
      <div className="App">
        <header className='App-header'></header>
        <DisplayGodos reyes={this.state.godos}></DisplayGodos>
      </div>
    );
  }
}

function DisplayGodos(props){
  const losreyes = props.reyes;
  const lista = losreyes.map((rey)=><Flashcard 
  key={rey.id}
  imagen={rey.imagen} 
  titulo={rey.nombre}
  texto={rey.texto}
/>);
return(
  <ul>
    {lista}
  </ul>
);
}
export default App;
