import logo from './logo.svg';
import './App.css';
import "bootstrap/dist/css/bootstrap.min.css";
import { Button } from 'reactstrap';
import { Component } from 'react';

class App extends Component{
  constructor(props){
    super(props)
    this.state={
      colores:Array(5).fill('secondary')
    }

}
cambiarColor(numBotoncito){
  if(this.state.colores[numBotoncito]=='secondary'){
    this.setState({colores:'info'})
  }
}



  render(){
    return (
      <>
        <Button color={this.state.colores[0]} onClick={()=>this.pulsado()}> Uno</Button>
        <Button color={this.state.colores[1]}onClick={()=>this.pulsado()}> Dos</Button>
        <Button color={this.state.colores[2]}onClick={()=>this.pulsado()}> Tres</Button>
        <Button color={this.state.colores[3]}onClick={()=>this.pulsado()}> Cuatro</Button>
        <Button color={this.state.colores[4]}onClick={()=>this.pulsado()}> Cinco</Button>

      </>
    );
  }
}

export default App;
