import logo from './logo.svg';
import './App.css';
import "bootstrap/dist/css/bootstrap.min.css";
import { Button } from 'reactstrap';
import { Component } from 'react';

class App extends Component{
  constructor(props){
    super(props)
    this.state={
      colores:Array(5).fill('secondary')
    }

}
cambiarColor(numBotoncito){
  let copia = this.state.colores;
  if(this.state.colores[numBotoncito]=='secondary'){
    copia[numBotoncito]="info";
  }else{
    copia[numBotoncito]="secondary";
  }
  this.setState({colores:copia})
}



  render(){
    return (
      <>
        <Button color={this.state.colores[0]} onClick={()=>this.cambiarColor(0)}> Uno</Button>
        <Button color={this.state.colores[1]}onClick={()=>this.cambiarColor(1)}> Dos</Button>
        <Button color={this.state.colores[2]}onClick={()=>this.cambiarColor(2)}> Tres</Button>
        <Button color={this.state.colores[3]}onClick={()=>this.cambiarColor()}> Cuatro</Button>
        <Button color={this.state.colores[4]}onClick={()=>this.cambiarColor()}> Cinco</Button>

      </>
    );
  }
}

export default App;
