import React, { useState } from 'react';
import { Button, Form, FormGroup, Label, Input, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { users } from "../data/users"; 

const Login = (props) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleChange = (e) => {
        const { name, value } = e.target;
        if (name === 'username') {
            setUsername(value);
        } else if (name === 'password') {
            setPassword(value);
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        // Validar que los campos no estén vacíos
        if (username.trim() === '' || password.trim() === '') {
            setError("Por favor, completa todos los campos.");
            return;
        }

        // Buscar en el array de usuarios
        const userFound = users.find(
            (user) => user.username === username && user.password === password
        );

        if (!userFound) {
            setError("Usuario no registrado o contraseña incorrecta.");
            return;
        }

        // Login exitoso
        setError('');
        console.log("Usuario autenticado:", userFound);
        // Aquí podrías guardar el usuario en el estado global o redireccionar
        props.toggle(); // Cierra la ventana modal

        // Limpiar campos
        setUsername('');
        setPassword('');
    };

    return (
        <Modal isOpen={props.mostrar} toggle={props.toggle}>
            <ModalHeader toggle={props.toggle}>Iniciar Sesión</ModalHeader>
            <ModalBody>
                {error && <p style={{ color: 'red' }}>{error}</p>}
                <Form onSubmit={handleSubmit}>
                    <FormGroup>
                        <Label for="username">Usuario:</Label>
                        <Input
                            type="text"
                            name="username"
                            id="username"
                            placeholder="Ingresa tu usuario"
                            value={username}
                            onChange={handleChange}
                        />
                    </FormGroup>
                    <FormGroup>
                        <Label for="password">Contraseña:</Label>
                        <Input
                            type="password"
                            name="password"
                            id="password"
                            placeholder="Ingresa tu contraseña"
                            value={password}
                            onChange={handleChange}
                        />
                    </FormGroup>
                </Form>
            </ModalBody>
            <ModalFooter>
                <Button color="primary" onClick={handleSubmit}>Ingresar</Button>{' '}
                <Button color="secondary" onClick={props.toggle}>Cancelar</Button>
            </ModalFooter>
        </Modal>
    );
};

export default Login;
