import React, { Component } from "react";
import { users } from "../data/users"; 

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: "",
            password: "",
            error: "",
        };
    }

    // Maneja el cambio en los inputs
    handleChange = (e) => {
        this.setState({ [e.target.name]: e.target.value });
    };

    // Valida y realiza el login
    handleLogin = () => {
        const { username, password } = this.state;
        const { onClose } = this.props;

        // Validar que los campos no estén vacíos
        if (username.trim() === "" || password.trim() === "") {
            this.setState({ error: "Por favor, completa todos los campos." });
            return;
        }

        // Buscar en el array de usuarios si existen las credenciales
        const userFound = users.find(
            (user) => user.username === username && user.password === password
        );

        if (!userFound) {
            this.setState({ error: "Usuario no registrado o contraseña incorrecta." });
        } else {
            this.setState({ error: "" });
            console.log("Usuario autenticado:", userFound);
            onClose(); // Cierra la modal tras un login exitoso
        }
    };

    render() {
        const { show, onClose } = this.props;
        const { username, password, error } = this.state;

        // Si show es false, no renderiza la modal
        if (!show) return null;

        return (
            <div className="modal-backdrop">
                <div className="modal-content">
                    <button className="close-button" onClick={onClose}>
                        &times;
                    </button>
                    <h2>Iniciar Sesión</h2>
                    {error && <p className="error">{error}</p>}
                    <input
                        type="text"
                        name="username"
                        placeholder="Usuario"
                        value={username}
                        onChange={this.handleChange}
                    />
                    <input
                        type="password"
                        name="password"
                        placeholder="Contraseña"
                        value={password}
                        onChange={this.handleChange}
                    />
                    <button onClick={this.handleLogin}>Ingresar</button>
                </div>
            </div>
        );
    }
}

export default Login;
