import React, { useState } from 'react';
import { Button, Form, FormGroup, Label, Input, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { USUARIOS } from "../data/users.js"; 

const Login = (props) => {
    console.log('Users:', USUARIOS);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleChange = (e) => {
        const { name, value } = e.target;
        if (name === 'username') {
            setUsername(value);
        } else if (name === 'password') {
            setPassword(value);
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (username.trim() === '' || password.trim() === '') {
            setError("Por favor, completa todos los campos.");
            return;
        }

        const userFound = users.find(
            (user) => user.username === username && user.password === password
        );

        if (!userFound) {
            setError("Usuario no registrado o contraseña incorrecta.");
            return;
        }

        setError('');
        console.log("Usuario autenticado:", userFound);
        props.toggle();
        setUsername('');
        setPassword('');
    };

    return (
        <Modal isOpen={props.mostrar} toggle={props.toggle}>
            <ModalHeader toggle={props.toggle}>Login</ModalHeader>
            <ModalBody>

                <Form onSubmit={handleSubmit}>
                    <FormGroup>
                        <Label for="username">Usuario:</Label>
                        <Input
                            type="text"
                            name="username"
                            id="username"
                            placeholder="Username"
                            value={username}
                            onChange={(e) => setUsername(e.target.value)}
                        />
                    </FormGroup>
                    <FormGroup>
                        <Label for="password">Password:</Label>
                        <Input
                            type="password"
                            name="password"
                            id="password"
                            placeholder="Password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                    </FormGroup>
                </Form>
                {error && <p style={{ color: 'red' }}>{error}</p>}
            </ModalBody>
            <ModalFooter>
                <Button color="primary" onClick={handleSubmit}>Ingresar</Button>{' '}
                <Button color="secondary" onClick={props.toggle}>Cancel</Button>
            </ModalFooter>
        </Modal>
    );
};

export default Login;
