import React, { useState } from "react";
import { users } from "./data/users"; // Ajusta la ruta según la estructura de tu proyecto

const Login = ({ show, onClose }) => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");

    const handleLogin = () => {
        // Validar que los campos no estén vacíos
        if (username.trim() === "" || password.trim() === "") {
            setError("Por favor, completa todos los campos.");
            return;
        }

        // Buscar si existe un usuario con las credenciales ingresadas
        const userFound = users.find(
            (user) => user.username === username && user.password === password
        );

        if (!userFound) {
            setError("Usuario no registrado o contraseña incorrecta.");
        } else {
            setError("");

            console.log("Usuario autenticado:", userFound);
            onClose(); 
        }
    };

    // Si no se debe mostrar la modal, retorna null
    if (!show) return null;

    return (
        <div className="modal-backdrop">
            <div className="modal-content">
                <button className="close-button" onClick={onClose}>
                    &times;
                </button>
                <h2>Login</h2>
                {error && <p className="error">{error}</p>}
                <input
                    type="text"
                    placeholder="Username"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <button onClick={handleLogin}>Ingresar</button>
            </div>
        </div>
    );
};

export default Login;
